import React, {Component} from "react";
import Jumbotron from "../components/Jumbotron";
import Card from "../components/Card";
import Form from "../components/Form";
import Book from "../components/Book";
import Footer from "../components/Footer";
import API from "../utils/API";
import {Col, Row, Container} from "../components/Grid";
import {List} from "../components/List";
import Nav from "../components/Nav";
// import axios from "axios";

class Home extends Component {
  state = {
    books: [],
    image: null,
    query: "",
    favorites: [],
    message: "Search For A Movie To Begin!"
  };

  resetState = () => {
    this.setState({
      books: [],
      image: null,
      query: "",
      message: "Search For A Book To Begin!"
    })
  };

  handleInputChange = event => {
    const {name, value} = event.target;
    // console.log(name);
    // console.log(value);
    this.setState({
      [name]: value
    });
  };

  getMovie = () => {
    API.getMovie(this.state.query)
      .then(res => {
       const image = this.getImage(res.data.TitleName);
       console.log("Bingo ====>",image);
       let movie = res.data;
       movie.image = image;
        // console.log(res.data);
        this.setState({
          books: [...this.state.books, movie]
        });
        console.log("logging state ->", this.state.books);
      })
      .catch(() =>
        this.setState({
          books: [],
          message: "No New Movies Found, Try a Different Query"
        })
      );
  };

  getImage = title => {
    API.image(title)
      .then(res => {
        // console.log("inideImage return --> ", res.data.Poster);
        return res.data.Poster;
      })
      .catch(err => console.log(err.message));
  };

  handleFormSubmit = event => {
    event.preventDefault();
    this.resetState();
    this.getMovie();

  };

  handleSavedMovies = (book, img) => {
    book.image = img;
    console.log("chicken --->", book, img);
    this.setState( {favorites: [...this.state.favorites, book]});

    // const book = this.state.books.find(book => book.id === id);

    /*API.saveBook({
      googleId: book.id,
      title: book.volumeInfo.title,
      subtitle: book.volumeInfo.subtitle,
      link: book.volumeInfo.infoLink,
      authors: book.volumeInfo.authors,
      description: book.volumeInfo.description,
      image: book.volumeInfo.imageLinks.thumbnail
    }).then(() => this.getMovie());*/
  };

  render() {
    return (
      <>
      <Nav />
      <Container>
        <Row>
          <Col size="md-12">
            <Jumbotron>
              <h1 className="text-center">
                <strong>(React) Movie Search</strong>
              </h1>
              <h2 className="text-center">Search for and Save Movies of your Interest.</h2>
            </Jumbotron>
          </Col>
          <Col size="md-12">
            <Card title="Movie Search" icon="fas fa-video">
              <Form
                handleInputChange={this.handleInputChange}
                handleFormSubmit={this.handleFormSubmit}
                query={this.state.query}
              />
            </Card>
          </Col>
        </Row>
        <Row>
          <Col size="md-12">
            <Card title="Results">
              {this.state.books.length ? (
                <List>
                  {this.state.books.map(book => (
                    <Book
                      key={book.TitleName}
                      TitleName={book.TitleName}
                      description={book.Storylines[0].Description}
                      image={book.image}
                      /*
                      TODO Reuse the code to render more stuff
                      subtitle={book.volumeInfo.subtitle}
                      link={book.volumeInfo.infoLink}
                      authors={book.volumeInfo.authors.join(", ")}
                      description={book.volumeInfo.description}
                      image={book.volumeInfo.imageLinks.thumbnail}*/
                      Button={() => (
                        <button
                          onClick={() => this.handleSavedMovies(book)}
                          className="btn btn-primary ml-2 pull-right"
                        >
                          Save
                        </button>
                      )}
                    />
                  ))}
                </List>
              ) : (
                <h2 className="text-center">{this.state.message}</h2>
              )}
            </Card>
          </Col>
        </Row>
        <Footer/>
      </Container>
        </>
    );
  }
}

export default Home;
